import {
  createStyles,
  MantineNumberSize,
  getSizeValue,
  MantineShadow,
  getFocusStyles,
} from '@mantine/styles';

interface PopoverBodyStyles {
  shadow: MantineShadow;
  radius: MantineNumberSize;
  spacing: MantineNumberSize;
}

export default createStyles((theme, { radius, shadow, spacing }: PopoverBodyStyles) => ({
  title: {},

  wrapper: {
    ...getFocusStyles(theme),
  },

  popover: {
    background: theme.colorScheme === 'dark' ? theme.colors.dark[6] : theme.white,
    pointerEvents: 'all',
    borderRadius: getSizeValue({ size: radius, sizes: theme.radius }),
  },

  body: {
    border: `1px solid ${
      theme.colorScheme === 'dark' ? theme.colors.dark[6] : theme.colors.gray[2]
    }`,
    boxShadow: shadow in theme.shadows ? theme.shadows[shadow] : shadow,
    borderRadius: getSizeValue({ size: radius, sizes: theme.radius }),
  },

  inner: {
    padding: getSizeValue({ size: spacing, sizes: theme.spacing }),
  },

  header: {
    borderBottom: `1px solid ${
      theme.colorScheme === 'dark' ? theme.colors.dark[7] : theme.colors.gray[2]
    }`,
    padding: `${theme.spacing.xs / 1.5}px ${getSizeValue({
      size: spacing,
      sizes: theme.spacing,
    })}px`,
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'space-between',
  },

  close: {
    position: 'absolute',
    top: 7,
    zIndex: 2,
    right: 10,
  },
}));
