import {
  createStyles,
  getSharedColorScheme,
  MantineNumberSize,
  getSizeValue,
  MantineColor,
  getFocusStyles,
} from '@mantine/styles';

interface PaginationStyles {
  size: MantineNumberSize;
  radius: MantineNumberSize;
  color: MantineColor;
}

const sizes = {
  xs: 22,
  sm: 26,
  md: 32,
  lg: 38,
  xl: 44,
};

export default createStyles((theme, { size, radius, color }: PaginationStyles, getRef) => {
  const dots = {
    ref: getRef('dots'),
    cursor: 'default',
    borderColor: 'transparent',
    backgroundColor: 'transparent',
  } as const;

  const colors = getSharedColorScheme({
    color,
    theme,
    variant: 'filled',
  });

  return {
    item: {
      ...getFocusStyles(theme),
      cursor: 'pointer',
      userSelect: 'none',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      fontWeight: 500,
      border: `1px solid ${
        theme.colorScheme === 'dark' ? theme.colors.dark[5] : theme.colors.gray[3]
      }`,
      color: theme.colorScheme === 'dark' ? theme.colors.dark[0] : theme.black,
      height: getSizeValue({ size, sizes }),
      minWidth: getSizeValue({ size, sizes }),
      padding: `0 ${getSizeValue({ size, sizes: theme.spacing }) / 2}px`,
      fontSize: getSizeValue({ size, sizes: theme.fontSizes }),
      borderRadius: getSizeValue({ size: radius, sizes: theme.radius }),
      lineHeight: 1,
      backgroundColor: theme.colorScheme === 'dark' ? theme.colors.dark[5] : theme.white,

      [`&:active:not(:disabled):not(.${dots.ref})`]: {
        transform: 'translateY(1px)',
      },

      '&:disabled': {
        opacity: 0.6,
        cursor: 'not-allowed',
        color: theme.colorScheme === 'dark' ? theme.colors.dark[3] : theme.colors.gray[5],
      },
    },

    active: {
      borderColor: 'transparent',
      color: colors.color,
      backgroundColor: colors.background,
    },

    dots,
  };
});
